import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { HttpService } from '../service/http.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-createarticle',
  templateUrl: './createarticle.component.html',
  styleUrls: ['./createarticle.component.scss']
})
export class CreatearticleComponent {
  img:any;
  article:any={
    title:'',
    content:'',
    tags:[],
    description:''
  }
  constructor(private http:HttpService,private router:Router){}
  tag:any='';
  select(e:any){
    this.img=e.target.files[0]
  }
  create(){
    let fd =new FormData()
      fd.append('title',this.article.title)
    fd.append('content',this.article.content)
    fd.append('tags',this.article.tags.toString())
    fd.append('description',this.article.description)
    fd.append('image',this.img)
    fd.append('idAuthor',this.http.getAuthorDataFromToken()._id)
    this.http.create(fd).subscribe(
      res=>{
        this.router.navigate(['/home'])
      }
    )
  }

}
