import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class HttpService {

  constructor(private http:HttpClient){}
  articlesUrl='http://127.0.0.1:3000/article/'
  CreateUserAccount(model:any){
   return this.http.post('http://localhost:3000/users',model);
  }
  login(author:any){
   return this.http.get('http://localhost:3000/login',author);
  }
  isLoggedIn(){
    let token =localStorage.getItem('token')
      if(token){
        return true
      }else{
        return false
      }
  }
  getAuthorDataFromToken(){
    let token =localStorage.getItem('token')
    if(token){
      let data =JSON.parse(window.atob(token.split('.')[1]))
      return data;
    }
  }
  getById(id:any){
    return this.http.get('http://localhost:3000/users'+id)
  }
  create (article:any){
    return this.http.post(this.articlesUrl +'ajout',article)
  }
  getAllArticles(){
    return this.http.get(this.articlesUrl+'all')
  }
  getArticleByIdAuthor(id:any){
    return this.http.get(this.articlesUrl + 'getbyidauthor/' + id)
  }
  getArticleById(id:any){
    return this.http.get(this.articlesUrl + 'getbyid'+id)
  }

}
