import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpService } from 'src/app/services/httpRequest/http.service';
import { SendAndGetDataBetweenComponentsService } from 'src/app/services/send-and-get-data-between-components.service';

@Component({
  selector: 'app-author',
  templateUrl: './author.component.html',
  styleUrls: ['./author.component.scss']
})
export class AuthorComponent implements OnInit{
  data:any;
  Articles:any;
  darkmode!:boolean;

  constructor(private ar:ActivatedRoute,private http:HttpService,private getData:SendAndGetDataBetweenComponentsService){}
  ngOnInit(): void {
    this.getData.getTheme().subscribe(theme=>this.darkmode=theme)
    this.getUserDate();
    this.getUserArticles()
  }
  getUserDate(){
    this.http.getSpecificUser(this.ar.snapshot.paramMap.get('id')).subscribe((data:any)=>this.data=data)
  }
  getUserArticles(){
    this.http.getAllArticles().subscribe((articles:any)=>{this.Articles=articles
      this.Articles= this.Articles.filter((article:any)=>article.userId==this.ar.snapshot.paramMap.get('id'))
    })
  }

}
<div class="author" [class.light]="!darkmode">
    <div class="profilebox">
        <img [src]="data?.imgUrl" alt="">

    <div class="about">
        <h2 class="name">{{data?.firstname}} {{data?.lastname}}</h2>
        <h4><span>{{Articles.length}}</span> article</h4>
        <p>{{data.about}}</p>
             <button routerLink="/createArticle">
                Create A New Article
            </button>
    </div>
</div>
<section class="bloglist">
    <div class="title"><h3>Your Articles</h3></div>
    <div class="articles">
        <div class="article"*ngFor="let Article of Articles">
            <ng-container  *ngIf="Articles?.length">
            <img [src]="Article.img" alt="" >
            <div class="articlecontent">
                <div class="date"><i class="fa-regular fa-calendar"></i> <p>may 25 , 2022</p></div>
                <div class="title">
                    <h4><a href="" routerLink="/details/{{Article?.id}}">{{Article.title}}</a></h4>
                </div>
                <div class="content">
                    <p>{{Article.description}}</p>
                </div>
            </div></ng-container>
        </div>
    </div>
    <h2 *ngIf="!Articles?.length">you do not have any article yet</h2>
</section>


</div>
